import React from 'react';
import ButtonMath from './ButtonMath';

import { css } from 'emotion'


const AboutMe = () => {
    return(
    <div className={styles.about}>
        <h1>Hi! I am Patricia Shirazi</h1>
        <p>
            I am a former business owner, former Math Teacher and actual Software Engineer.
             At the beggining I thought maybe it would not go together, but I think my background blended well and give me and
             interesting skill combination. You can explore a little of my carrer on the links bellow!
        </p>
        <div className={styles.buttons_container}>
        <ButtonMath />
      </div>
    </div>
    )}

const styles = {
    about: css`
    position: absolute;
    widht: 700px;
    text-align: center;
    top: 130%;
    `,
    buttons_container: css`
      margin-top: 5%;
      margin-left: 10%;
      width: 700px;
      display: flex;
      justify-content: space-between;
  `,
  }

export default AboutMe;
