import React, { useState } from 'react';
import { css } from 'emotion'

import Carousel from './Carousel';



const Subjects = (props) => {
    const [subject, setSubject] = useState('')

    const onclick = (e) => {
        setSubject(e.target.value);
    }

    return (
        <div className={styles.test}>
            <div id={"here"} className={styles.buttons_container}>
                <button onClick={onclick} className={styles.button} value="All"> All </button>
                <button onClick={onclick} className={styles.button} value="Math"> Math </button>
                <button onClick={onclick} className={styles.button} value="Google"> Google </button>
                <button onClick={onclick} className={styles.button} value="Engineering"> Software Engineering </button>
                <button onClick={onclick} className={styles.button} value="Entrepreneurship"> Entrepreneurship </button>
                <button onClick={onclick} className={styles.button} value="Entrepreneurship"> Voluntering </button>
            </div>
                < Carousel matter={subject} showMoody={props.showMoody} showSubject={props.showSubject} showGoogle={props.showGoogle} showGraduation={props.showGraduation} showMath={props.showMath}/>
        </div>
    )
}

const styles = {
    test: css`
    display:flex;
    @media (max-width: 800px) {
        margin: auto;
    }
    `,
    buttons_container: css`
        margin-top: 5%;
        margin-left: 10.5%;
        flex-wrap: wrap;
        width: 1000px;
        display: flex;
        justify-content: space-between;
        @media (max-width: 800px) {
            margin-top: 5%;
            margin: auto;
            flex-direction: column;
            transition: 1s;
            width: 300px;
            justify-content: space-between;
        }
        `,
    button: css` 
        border: 2px solid black;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
        &:hover {
         background-color: black;
        color: white;
        transition: 1s;
         border-color:#DDDDDD;
        }&:focus {
            outline: none;
             background-color: black;
            color: white;
        }@media (max-width: 1400px) {
            margin-top: 3%;
        }
        `,
}
export default Subjects;
