import React from 'react';
import {css} from 'emotion'
import { MDBView, MDBMask} from "mdbreact";

import graduation from '../graduation.jpg'




const Grid = () => {
    return(
        <MDBView hover zoom>
            <img 
            src={graduation}
            className={styles.graduation} 
            alt=""
            />
            <MDBMask className="flex-center" overlay="red-strong">
            <p>Graduation</p>
            </MDBMask>
        </MDBView>
    )
}

const styles = {
    grid: css`
    display: flex;
    width: 60%;
    margin-right: 40%;
    `,
    graduation: css`
    width: 150px;
    margin-right: 40%;

    `
}
export default Grid;
