import React from 'react';
import { MDBView, MDBMask } from "mdbreact";
import {css} from 'emotion'

import graduation from '../graduation.jpg'




const Grid = () => {
    return(
        <div className={styles.grid}>
            <MDBView hover zoom>
                <img className={graduation} src={graduation}
                />
                <MDBMask>
                    <p>Graduation</p>
                </MDBMask>
            </MDBView>

        </div>
    )
}

const styles = {
    grid: css`
    display: flex;
    width: 60%;
    margin-right: 40%;
    `,
    graduation: css`
    width: 150px;
    `
}
export default Grid;
