import React from 'react';
import { css } from 'emotion'

import background from '../images/back.png'
import me from '../images/me.png'



const Intro = () => {
    return (
        <div>
            <div className={styles.intro1_holder}>    </div>
            <div className={styles.intro2_holder}>
                <img className={styles.img} src={me} />
                <div className={styles.textHolder}>
                    <div> className={styles.myname} PATRICIA SHIRAZI <br></br>
                        <div className={styles.description}>Full Stack Software Engineer</div>
                    </div>
                </div>
            </div>
        </div>
    )
}

const styles = {
    intro2_holder: css`
    display: none;
    @media (max-width: 1300px) {
        display: flex;
        justify-content: center;
        color: #cb006d;
        height: 100vh;
        width: 100%;
        // background-color: #9ed0c9;  
        background-color: black; 
    }
    `,
    name: css`
    // position: absolute;
    // top: 60%;
    // left: 2%;
     display: flex;
    flex-wrap: wrap-reverse;
    justify-content: space-between;
    align-items: center;
    // width: 400px;
    // margin:auto;
    // margin-top: 50%;
    height: 100px;
    font-size: 25px;
    font-weight: bold;
    font-family: "Trebuchet MS", Helvetica, sans-serif
    // @media (max-width: 812px) {
    //     justify-content: space-between;
    //     flex-direction: row;
    //     width: 70%;
    //     margin-top: 1%;
    //     height: 700px;
    //     font-size: 25px;
    //     font-weight: bold;
    //     font-family: "Trebuchet MS", Helvetica, sans-serif
    // }

        `,
    st: css`
    font-size: 14px;
    color: brown;

    `,
    textHolder: css`
    margin-left: 250%;
    margin-top: 10px;
    width: 700px;
    background-color: white;
    `,

    img: css`
    width: auto;
    height: auto;
    margin:auto;
    @media (min-width: 812px) {
        width: 135%;
        margin-left: 2%;
    }
    `,
    intro1_holder: css`
    display: none;
    @media (min-width: 1300px) {
       display:block;
       height: 100vh;
       background-image: url(${background});
       background-color: #9ed0c9;
       background-repeat: no-repeat;
       width: 100%;
    }
    `,
}

export default Intro;
