import React, { useRef } from 'react';
import { css } from 'emotion'


const Contact = (props) => {

    return (
            <div ref={props.Cref} >
                <div className={styles.contact}>
                    <h2 className={styles.title}>  Contact </h2>
                    <p className={styles.line}></p>
                    <br></br>
                    <p className={styles.description}> Any questions about a project? send me an email </p>
                </div>
            </div>
    )
}

const styles = {
    contact: css`
        margin-top: 20%;
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    `,
    title: css`
        font-size: 22px;
    `,
    line: css`
        border-bottom: 1px solid grey;
        padding-bottom: 5px;
        margin: auto;
        width: 100px;
    `,
    description: css`
        font-size: 18px;
    `

}

export default Contact;
