import React from 'react';
import { css } from 'emotion'


import Moody_text from './Moody_text';



const Article_Header = (props) => {
    if (props.showMoody) {
        return (
            <div>
                <header className={styles.title_holder}>
                    <h1 className={styles.title}>{props.title}</h1>
                    <h5 className={styles.description}> {props.description}</h5>
                </header>
                <Moody_text/>
            </div>
        )
    }
}


const styles = {
    title_holder: css` 
        width: 100%;
        height: 180px;
        background-color: black;
        color: white;
        `,
    title: css`
        padding-top: 25px;
        padding-left: 48px;
        font-family: 'Raleway',sans-serif;
        font-weight: 800;
        font-size: 35px;
        `,
    description: css`
        padding-left: 48px;
        font-family:  Montserrat,-apple-system,BlinkMacSystemFont,Segoe UI,Fira Sans,Helvetica Neue,Arial,sans-serif;
        font-weight: 600;
        font-size: 15px;
    `,
}

export default Article_Header;
