import React, { useState } from 'react';
import { css } from 'emotion'

import Grid from './Grid';



const ButtonMath = () => {
    const [subject, setSubject] = useState('')

    const onclick = (e) => {
        setSubject(e.target.value);
    }

    return (
        <div>
            <div className={styles.buttons_container}>
                <button onClick={onclick} className={styles.button} value="All"> All </button>
                <button onClick={onclick} className={styles.button} value="Math"> Math </button>
                <button onClick={onclick} className={styles.button} value="Google"> Google </button>
                <button onClick={onclick} className={styles.button} value="Engineering"> Software Engineering </button>
                <button onClick={onclick} className={styles.button} value="Entrepreneurship"> Entrepreneurship </button>
            </div>
            <div className={styles.grid}>
                < Grid matter={subject} />
            </div>
        </div>
    )
}
const styles = {
    grid: css`
    display: flex;
    justify-content:space-between;
    align-items: center;
    flex-wrap: wrap;
    width: 1200px;
    transition: width 2s;
    height: auto;
    // margin-top: 10%;
    margin-right: -10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1500px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    buttons_container: css`
        margin-top: 80%;
        margin-left: 23.5%;
        width: 700px;
        display: flex;
        justify-content: space-between;
        `,
    button: css` 
        border: 2px solid black;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
        &:hover {
         background-color: black;
        color: white;
        transition: 1s;
         border-color:#DDDDDD;
        }&:focus {
            outline: none;
             background-color: black;
            color: white;

        }
        `,
}
export default ButtonMath;
