import React from 'react';
import { css, keyframes } from 'emotion'

import Graduation from './Graduation';
import Business from './Business';
import Automation from './Automation';
import Google from './Google';
import Math from './Math';
import Moody from './Moody';





const Grid = (props) => {

    if (props.matter === "Math") {
        return (
            <div className={styles.grid_selected}>
                < Graduation />
                < Math />
            </div>
        )
    } if (props.matter === "Google") {
        return (
            <div className={styles.grid_selected}>
                < Google />
                < Automation />
            </div>
        )
    } if (props.matter === "Engineering") {
        return (
            <div className={styles.grid_selected}>
                < Automation />
                < Moody />
            </div>
        )
    } if (props.matter === "Entrepreneurship") {
        return (
            <div className={styles.grid_selected}>
                < Business />
            </div>
        )
    } return (
        <div className={styles.grid}>
            < Moody />
            < Business />
            < Automation />
            < Graduation />
            < Google />
            < Math />
        </div>
    )

}

const styles = {
    grid: css`
    display: flex;
    justify-content:flex-start;
    flex-wrap: wrap;
    width: 950px;
    height: 500px;
    margin-top: 10%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1500px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    grid_selected: css`
    display: flex;
    justify-content:flex-start;
    width: 1200px;
    height: 500px;
    margin-top: 10%;
    margin-left: 10%;
    margin-top: 25px;
    font-size: 21px;
    text-align: center;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 550px;
        transition: height 2s;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default Grid;
