import React from 'react';
import {css} from 'emotion'
import { MDBView, MDBMask} from "mdbreact";

import graduation from '../graduation.jpg'


const Graduation = () => {
    return (
        <div>
            <MDBView hover zoom>
                <img
                    className={styles.graduation}
                    src={graduation}
                    alt=""
                />
                {/* <MDBMask className={styles.flex_center}> */}
                    <p>Graduation</p>
                {/* </MDBMask> */}
            </MDBView>
        </div>
    )
}


const styles = {
    graduation: css`
    height: 100%;
    width: 35%;
    `,
    flex_center: css`{
    align-text: center;
    margin:auto;
    color: white;
    }`,
}
export default Graduation;
