import React, { useState } from 'react';
import { css } from 'emotion'
import ScrollIntoView from 'react-scroll-into-view'


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";




import Header from './components/Header';
import Intro from './components/Intro';
import Subjects from './components/Subjects';
import Moody_text from './components/carousel/Moody_text';


function App() {
  const [clickMoody, setClickMoody] = useState(false);

  if (!clickMoody) {
    return (
      <div>
        <div>
          <header>
            <Intro />
          </header>
          <div >
            <ScrollIntoView selector="#header" className={styles.scroll}>
              <button className={styles.button}> About me </button>
            </ScrollIntoView>
          </div>
        </div>
        <div className={styles.body}>
          < Header />
          <Subjects showMoody={setClickMoody} />
        </div>
      </div>

    );
  } else {
    return (
      <div>
        <Moody_text />
      </div>
    )
  }
}

const styles = {
  body: css`
  margin-top: 0%;
  `,
  button: css` 
  // margin-top: 600px;
  border-radius: 5px;
  padding: 14px 28px;
  cursor: pointer;
  &:hover {
  color: white;
  background-color: black;
  transition: 1s;
  窶ッorder-color:#DDDDDD;
  }&:focus {
      outline: none;
      窶ッackground-color: black;
      color: white;

  }
  `,
}

export default App;
