import React from 'react';
import {css} from 'emotion'
import { MDBView, MDBMask} from "mdbreact";

import graduation from '../graduation.jpg'




const Grid = () => {
    return(
        <div className={styles.grid}>
            <MDBView hover zoom>
                <img 
                src={graduation}
                className={styles.graduation} 
                alt=""
                />
                <MDBMask className="flex-center">
                <p>Graduation</p>
                </MDBMask>
            </MDBView>
        </div>
    )
}

const styles = {
    grid: css`
    background-color: pink;
    display: flex;
    width: 60%;
    margin-left: 10%;
    `,
    graduation: css`
    width: 150px;
    margin-right: 40%;

    `
}
export default Grid;
