import React from 'react';
import {css} from 'emotion'
import { MDBView, MDBMask} from "mdbreact";

import math from '../images/math.jpeg'

const Math = () => {
    return (
            <MDBView hover zoom>
                <img
                    className={styles.math}
                    src={math}
                    alt=""
                />
                <MDBMask className={styles.classname} overlay="cyan-light">
                    <p>High School Teacher</p>
                </MDBMask>
            </MDBView>
    )
}

const fadeInImage = keyframes ` {
    0% {
        width: 0%;
    } 
    30% {
        width: 20%;
    }
    70% {
        width: 70%;
    }
    100% {
        width: 100%;
    }
}`

let classname = "flex-center";

const styles = {
    math: css`
    height: 100%;
    width: 100%;
    animation: ${fadeInImage} 2s ease-in;
    // padding-top: 10%;
    `,
    classname: css`
    color: white;
    font-size: 1.5em;
    font-weight: bold;
    cursor: pointer;
    `
}
export default Math;
