import React, {useEffect} from 'react';
import { css } from 'emotion'


const ButtonMath = (props) => {

const onclick = () => {
    props.google ? props.setGoogle(false) : props.setGoogle(true)
    props.setMath(false)
}

    return (
        <div className={styles.buttons_container}>
            <button onClick={() => props.setMath(false)} className={styles.button}> All </button>
            <button onClick={() => props.math ? props.setMath(false) : props.setMath(true)} className={styles.button}> Math </button>
            {/* <button onClick={() => props.google ? props.setGoogle(false) : props.setGoogle(true)} className={styles.button} className={styles.button}> Google </button> */}
            <button onClick={onclick} className={styles.button} className={styles.button}> Google </button>
            <button className={styles.button}> Software Engineering </button>
            <button className={styles.button}> Entrepreneurship </button>
        </div>
    )
}
const styles = {
    buttons_container: css`
        margin-top: 25%;
        margin-left: 10%;
        width: 700px;
        display: flex;
        justify-content: space-between;
        `,
    button: css` 
        border: 2px solid black;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
        &:hover {
         background-color: black;
        color: white;
        transition: 1s;
         border-color:#DDDDDD;
        }&:focus {
            outline: none;
             background-color: black;
            color: white;

        }
        `,
}
export default ButtonMath;
