import React from 'react';
import { css } from 'emotion'

import Graduation from './Graduation';
import Business from './Business';
import Projects from './Projects';
import Google from './Google';
import Math from './Math';





const Grid = (props) => {

    if (props.matter === "Math") {
        return (
            <div className={styles.grid_selected}>
                < Graduation />
                < Business />
            </div>
        )
    } if (props.matter === "Google") {
        return (
            <div className={styles.grid_selected}>
                < Google />
                < Projects />
            </div>
        )
    } return (
        <div className={styles.grid}>
            < Graduation />
            < Business />
            < Projects />
            < Google />
            < Math />
        </div>
    )

}

const styles = {
    grid: css`
    display: flex;
    justify-content:space-between;
    flex-wrap: wrap;
    width: 1200px;
    transition: width 2s;
    height: auto;
    margin-top: 10%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1500px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    grid_selected: css`
    display: flex;
    justify-content:space-between;
    opacity: 1; 
    width: 550px;
    transition: width 2s;
    height: auto;
    margin-top: 10%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 550px;
        transition: height 2s;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default Grid;
