import React, { useState } from 'react';
import { css } from 'emotion'
import ScrollIntoView from 'react-scroll-into-view'


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";




import Header from './components/Header';
import Intro from './components/Intro';
import Subjects from './components/Subjects';
import Moody_text from './components/articles/Moody_text';
import Business_text from './components/articles/Business_text';
import Google_text from './components/articles/Google_text';
import Footer from './components/Footer';
import Graduation_text from './components/articles/Graduation_text';


let intViewportHeight = window.innerHeight;
let toString = (intViewportHeight - 100).toString() + "px"

function App() {
  const [clickMoody, setClickMoody] = useState(false);
  const [clickBusiness, setClickBusiness] = useState(false);
  const [clickGoogle, setCLickGoogle] = useState(false);
  const [clickGraduation, setCLickGraduation] = useState(false);


  if (clickMoody) {
    return (
      <Router>
        <Route exact path="/moody">
          <Moody_text showMoody={setClickMoody} />
          <Footer />
        </Route>
      </Router>
    )
  } if (clickBusiness) {
    return (
      <Router>
        <Route exact path="/business">
          <Business_text />
          <Footer />
        </Route>
      </Router>
    )
  } if (clickGoogle) {
    return (
      <Router>
        <Route exact path="/google">
          <Google_text />
          <Footer />
        </Route>
      </Router>
    )
  } if (clickGraduation) {
    return (
      <Router>
        <Route path="/hackreactor">
          <Graduation_text showGraduation={setCLickGraduation} />
          <Footer />
        </Route>
      </Router>
    )
  } else {
    return (
      <Router>
        <Route path="/home">
          <div>
            <Intro />
            <div>
              <ScrollIntoView selector="#header" className={styles.scroll}>
                <button className={styles.button}> About me </button>
              </ScrollIntoView>
            </div>
          </div>
          <div className={styles.body}>
            <Header />
            <Subjects showMoody={setClickMoody} showSubject={setClickBusiness} showGoogle={setCLickGoogle} showGraduation={setCLickGraduation}/>
            <Footer />
          </div>
        </Route>
      </Router>
    )
  }
}

const styles = {
  body: css`
                  margin-top: 0%;
                  `,
  button: css`
                  position: absolute;
  top: ${toString};
                  left: 45%;
                  border-radius: 5px;
                  padding: 14px 28px;
                  cursor: pointer;
  &:hover {
              color: white;
          background-color: black;
          transition: 1s;
           border-color:#DDDDDD;
  }&:focus {
              outline: none;
           background-color: black;
          color: white;

      }
      `,
  scroll: css`
      background-color: blue;
      `
}

export default App;
