import React, { useState } from 'react';
import { TransitionGroup } from 'react-transition-group';
import { MDBView, MDBMask } from "mdbreact";



import { css } from 'emotion'

import htg from '../images/HTG.png';
import moody from '../images/m.png';


const animation = {
    // "example-enter" {
    //     opacity: 0.01;
    //   }

    // example-enter.example-enter-active {
    //     opacity: 1;
    //     transition: opacity 500ms ease-in;
    //   }

    //   .example-leave {
    //     opacity: 1;
    //   }

    //   .example-leave.example-leave-active {
    //     opacity: 0.01;
    //     transition: opacity 300ms ease-in;
    //   }
}

const Projects = () => {
    return (
        <div className={styles.projects}>
            <h2> Recent Projects </h2>
            <div className={styles.projects_grid}>
                <TransitionGroup
                 transitionName={styles.example}
                 transitionEnterTimeout={500}
                 transitionLeaveTimeout={300}
                >
                <MDBView hover zoom >
                    <img
                        // className={styles.image}
                        src={htg}
                        alt=""
                    />
                    <MDBMask overlay="cyan-light" className={styles.project_title} >
                        <p>Humboldt To Go</p>
                    </MDBMask>
                </MDBView>
                </TransitionGroup>

                <MDBView hover zoom>
                    <img
                        className={styles.image}
                        src={moody}
                        alt=""
                    />
                    <MDBMask overlay="cyan-light" className={styles.project_title} >
                        <p>Moody</p>
                    </MDBMask>
                </MDBView>
            </div>
        </div>

    )
}

const styles = {
    example: css`
    enter: opacity: 0.01;   
    `,
    projects: css`
        margin-top: 2%;
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-size: 22px;
    `,
    projects_grid: css`
        // border: 3px solid pink;
        width: 40%;
        display: flex;
        justify-content: space-around;
        flex-wrap: wrap;
        margin-top: 10px;

    `,
    project_title: css`
        color: white;
        cursor: pointer;
        font-size: 1.0em;
        font-weight: bold;
        text-align: center;

    `

}


export default Projects;
