import React from 'react';
import TextLoop from "react-text-loop";
import { css } from 'emotion'


import maname from '../images/maname.png'


const symbol = "<"

const Maname = () => {
    return (
        // <div className={styles.header}>
            <div>
            <div>
                {/* <img className={styles.maname} src={maname} /> */}
                <h1 className={styles.maname}> Olá, </h1>
            </div>
            <h2 className={styles.text}>
                <TextLoop  springConfig={{ stiffness: 70, damping: 31 }}
        adjustingSpeed={500} >
                    <span> Software Engineering </span>
                    <span> Mathematics </span>
                    <span> Entrepreneurship </span>
                    <span> User Experience </span>
                </TextLoop>{" "}
            </h2>
            {/* <button className={styles.button} > About me </button> */}
        </div>
    )
}
export default Maname;

const styles = {
    maname: css`
    display: flex;
    align-content: center;
    z-index: -3;
    // width: 40%;
    // height: 60%;
    // margin-right: 40%;
    // margin-top: 10%;
    font-size: 67px;
    margin: 0 auto;
    // width: 50%;
    color: black;
    // border: 3px solid green;
    padding: 10px;
    font-family: 'Libre Franklin', sans-serif;
    `,
    text: css`
    margin-right: 40%;
    color: gray;
    `,
    header: css `
    background-color: black;
    height: 60px;
    padding: 10px;
    color: white;
    `,
    button: css` 
    position: absolute;
    border-radius: 5px;
    top: 85%;
    padding: 14px 28px;
    cursor: pointer;
    &:hover {
    color: white;
     background-color: black;
    transition: 1s;
     border-color:#DDDDDD;
    }&:focus {
        outline: none;
         background-color: black;
        color: white;

    }
    `,
}
