    height: auto;
    margin-top: 30%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 550px;
        transition: height 2s;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default ButtonMath;
