import React from 'react';
import { css } from 'emotion'


import Graduation from './Graduation';
import Business from './Business';

const ButtonMath = () => {
    return (
        <div>
            <button> Math </button>
            <button> Google </button>
            <button> Software Engineering </button>
            <button> Entrepreneurship </button>
        </div>
    )
}
const styles = {
    grid: css`
    display: flex;
    justify-content:space-between;
    flex-wrap: wrap;
    width: 1200px;
    transition: width 2s;
    height: auto;
    margin-top: 30%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1500px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    grid_selected: css`
    display: flex;
    justify-content:space-between;
    width: 550px;
    transition: width 2s;
    height: auto;
    margin-top: 30%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 550px;
        transition: height 2s;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default ButtonMath;
