import React from 'react';
import { css } from 'emotion'

import background from '../images/back.png'
import me from '../images/image.png'



const Intro = () => {
    return (
        <div>
            <div className={styles.intro1_holder}>    </div>
            <div className={styles.secondBackground}>
                <div className={styles.intro2_holder}>
                    <img className={styles.img} src={me} />
                    <div className={styles.nameHolder}>
                        <div className={styles.myname}> PATRICIA SHIRAZI <br></br>
                            <div className={styles.description}>Full Stack Software Engineer</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

const styles = {
    intro2_holder: css`
    height: 50vh;
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;

    }
    `,
    secondBackground: css`
    display: none;
    @media (max-width: 800px) {
        display: flex;
        height: 100vh;
        width: 100%;
        background-color: #9ed0c9;  
    `,


    img: css`
    width: 50vw;
    height: auto;
    margin:auto;
    @media (max-height: 375px) {

        height:100vh;
    }`,

    myname: css`
    margin-left: 10%;
    color: #cb006d;
    color: white;
    `,
    nameHolder: css`
    margin: auto;
   `,
    description: css`
    font-size: 14px;
    color: brown;
    `,
    myname: css`
    font-size: 25px;
    font-weight: bold;
    font-family: "Trebuchet MS", Helvetica, sans-serif
    `,
    intro1_holder: css`
    display: none;
    @media (min-width: 800px) {
       display:block;
       height: 100vh;
       background-image: url(${background});
       background-color: #9ed0c9;
       background-repeat: no-repeat;
       width: 100%;
    }
    `,
}

export default Intro;
