import React, { useState } from 'react';
import {css} from 'emotion'
import Modal from 'react-responsive-modal';
import cabeca from '../cabeca.png'
import graduation from '../graduation.jpg'




function University() {
    const [MyModal, setMyModal] = useState(false)
    return (
        <div>
            <img onClick={() => setMyModal(true)} className={styles.head} src={cabeca} alt="Patricia's Face"/>
            <p> I L0V3 Math </p>
            <Modal className={styles.styles} open={MyModal} onClose={() => setMyModal(false)} little>
            <h2> I graduated in Math Education in 2013 and published my first article</h2>
            {/* <img src={graduation} /> */}
            </Modal>
        </div>
    )
}
export default University;

const styles = {
    head: css `
        cursor: pointer;
    `,
    styles: css `
        margin-top: 40%;
    `

}
