import React from 'react';
import {css} from 'emotion'
import { MDBView, MDBMask, MDBContainer } from "mdbreact";

import graduation from '../graduation.jpg'




const Grid = () => {
    return(
        // <div className={styles.grid}>
            <MDBContainer>
            <MDBView hover zoom>
                <img className={styles.graduation} src={graduation}
                />
                <MDBMask className="flex-center" overlay="red-strong">
                    <p>Graduation</p>
                </MDBMask>
            </MDBView>
         </MDBContainer>
        // </div>
    )
}

const styles = {
    grid: css`
    display: flex;
    width: 60%;
    margin-right: 40%;
    `,
    graduation: css`
    width: 150px;
    margin-right: 40%;

    `
}
export default Grid;
