import React, { useState } from 'react';
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';

// import withCaption from 'react-awesome-slider/dist/captioned.css';
// node_modules/react-awesome-slider/dist/captioned.css
import { css } from 'emotion'

// const CaptionedSlider = withCaption(AwesomeSlider);
import python from '../images/python.png'
import business from '../images/business.jpg'
import google from '../images/google.jpg'




const Journey = () => {
    return (
        <div className={styles.container_slides_title}>
            <h2 className={styles.title}> My Journey </h2>
            <div className={styles.slide_container}>
                <div>
                    <AwesomeSlider
                        fillParent={false}
                        organicArrows={true}
                        play={true}
                        cancelOnInteraction={false} // should stop playing on user interaction
                        interval={6000}
                        className={styles.slide}
                    >
                        {/* <div className={styles.slides}> */}
                        <div className={styles.slides_container}>
                            <div className={styles.pic_description}>
                                <img src={google} />
                                <div className={styles.description}>
                                    <strong>I worked for 1 and a half years as a Quality Engineer at Google helping to improve Google Search results in Brazil.
                                    I became an expert on testing as well as using tools such as Selenium and Python to automate processes.
                    </strong>
                                </div>
                                {/* </div> */}
                            </div>
                        </div>
                        <div className={styles.slides_container}>
                            <div className={styles.pic_description}>
                                <img src={google} />
                                <div className={styles.description}>
                                    <strong>I worked for 1 and a half years as a Quality Engineer at Google helping to improve Google Search results in Brazil.
                                    I became an expert on testing as well as using tools such as Selenium and Python to automate processes.
                    </strong>
                                </div>
                                {/* </div> */}
                            </div>
                        </div>

                    </AwesomeSlider>
                </div>
            </div>
        </div>

    )
}
const styles = {
    container_slides_title: css`
        margin-top: 5%;
        width: 100%;
        height: 600px;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        text-align: center;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-size: 22px;
    `,
    // slide_container: css`
    // // border: 3px solid blue;
    // // width: 100%;
    // // height: 40px
    // // display: flex;
    // // justify-content: center;
    // // align-items: center;
    // // flex-direction: column;
    // align-text: center;


    // `,
    slide: css`
        //  border: 3px solid red;
        margin: auto;
        top:0px;
        height: 30%;


        // padding-botton: 50px;

    `,

    pic_description: css`
    //  border: 3px solid red;

        width: 80%;
        heigth: 20%;
        // margin-left: 0;
    `,
    description: css`
    font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    font-size: 17px;
    // margin: auto;
        align-text: center;
    `,
    slides_container: css`
    // border: 3px solid grey;
    margin-top: 0%;
    position: absolute;
    width: 100%;
    height: 60%;
    // left: 0;
    background-color:  #F5F5F5;
        display: flex;
        justify-content: center;
        align-items: center;
        flex-direction: column;
        img {
            float: right;
            border-radius: 50%
        }
    `

}

export default Journey
