import React, { useState } from 'react';
import { css } from 'emotion'
import ScrollIntoView from 'react-scroll-into-view'


import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";




import Header from './components/Header';
import Intro from './components/Intro';
import Subjects from './components/Subjects';
import Moody_text from './components/articles/Moody_text';
import Business_text from './components/articles/Business_text';


let intViewportHeight = window.innerHeight;
let toString = (intViewportHeight - 100).toString() + "px"

function App() {
  const [clickMoody, setClickMoody] = useState(false);
  const [clickBusiness, setClickBusiness] = useState(false);

  if (clickMoody) {
    return (
      <div>
        <Moody_text />
      </div>
    )
  } if (clickBusiness) {
    return (
      <div>
        <Business_text />
      </div>
    )
  } else {
    return (
      <Router>
        <Route path="/home">
          <div>
            <div>
              <Intro />
            </div>
            <div>
              <ScrollIntoView selector="#header" className={styles.scroll}>
                <button className={styles.button}> About me </button>
              </ScrollIntoView>
            </div>
          </div>
          <div className={styles.body}>
            < Header />
            <Subjects showMoody={setClickMoody} showSubject={setClickBusiness} />
          </div>
        </Route>
      </Router>
    )
  }
}

const styles = {
  body: css`
  margin-top: 0%;
  `,
  button: css` 
  position: absolute;
  top: ${toString};
  left: 45%;
  border-radius: 5px;
  padding: 14px 28px;
  cursor: pointer;
  &:hover {
  color: white;
  background-color: black;
  transition: 1s;
  窶ッorder-color:#DDDDDD;
  }&:focus {
      outline: none;
      窶ッackground-color: black;
      color: white;

  }
  `,
  scroll: css`
  background-color: blue;
  `
}

export default App;
