import React from 'react';
import { css } from 'emotion'

import background from '../images/back.png'
import phone from '../images/image.png'
import { text } from '@fortawesome/fontawesome-svg-core';



const Intro = () => {
    return (
        <div>
            <div className={styles.intro_holder}>    </div>
            <div className={styles.back}>
                <div className={styles.name}>
                    <div className={styles.textHolder}> PATRICIA SHIRAZI <br></br>
                        <div className={styles.st}>Full Stack Software Engineer</div>
                    </div>
                    <img className={styles.img} src={phone} />
                </div>
            </div>
        </div>
    )
}

const styles = {
    back: css`
    display: none;
    @media (max-width: 1300px) {
        display: flex;
        justify-content: center;
        color: #cb006d;
        height: 100vh;
        width: 100%;
        // background-color: #9ed0c9;  
        background-color: black; 
    }
    `,
    name: css`
    position: absolute;
    top: 60%;
    left: 3%;
     display: flex;
    flex-wrap: wrap-reverse;
    justify-content: space-between;
    align-items: center;
    width: 10%;
    margin:auto;
    // margin-top: 50%;
    height: 100px;
    font-size: 25px;
    font-weight: bold;
    font-family: "Trebuchet MS", Helvetica, sans-serif
    // @media (max-width: 812px) {
    //     justify-content: space-between;
    //     flex-direction: row;
    //     width: 70%;
    //     margin-top: 1%;
    //     height: 700px;
    //     font-size: 25px;
    //     font-weight: bold;
    //     font-family: "Trebuchet MS", Helvetica, sans-serif
    // }

        `,
    st: css`
    font-size: 14px;
    color: brown;

    `,
    textHolder: css`

    `,

    img: css`
    width: auto;
    height: auto;
    margin:auto;
    @media (min-width: 812px) {
        width: 135%;
        margin-left: 2%;
    }
    `,
    intro_holder: css`
    display: none;
    @media (min-width: 1300px) {
       display:block;
       height: 100vh;
       background-image: url(${background});
       background-color: #9ed0c9;
       background-repeat: no-repeat;
       width: 100%;
    }
    `,
}

export default Intro;
