import React, { useState } from 'react';
import { TransitionGroup } from 'react-transition-group';
import { MDBView, MDBMask } from "mdbreact";



import { css, keyframes } from 'emotion'

import htg from '../images/HTG.png';
import moody from '../images/m.png';
import portfolio from '../images/portfolio.png';
import tree from '../images/tree.png';





const Projects = (props) => {
    return (
        <div ref={props.projectsRef} className={styles.projects_container}  >
            <div className={styles.projects}>
                <h2> Recent Projects </h2>
                <p className={styles.line}></p>
                <br></br>
                <div className={styles.projects_grid}>
                    <div className={styles.project}>
                        <a
                            href={"https://www.humboldtogo.com/"}
                        >
                            <MDBView hover zoom >

                                <img
                                    // className={styles.image}
                                    src={htg}
                                    alt=""


                                />
                                <MDBMask overlay="cyan-light" className={styles.project_title} >
                                    <p>Humboldt To Go</p>
                                </MDBMask>
                            </MDBView>
                        </a>

                    </div>
                    <div className={styles.project}>
                        <a
                            href={"https://whispering-journey-70242.herokuapp.com/"}
                        >
                            <MDBView hover zoom>
                                <img
                                    className={styles.image}
                                    src={moody}
                                    alt=""

                                />
                                <MDBMask overlay="cyan-light" className={styles.project_title} >
                                    <p>Moody</p>
                                </MDBMask>
                            </MDBView>
                        </a>
                    </div>
                    <div className={styles.project}>

                        <MDBView hover zoom>
                            <img
                                className={styles.image}
                                src={tree}
                                alt=""
                            />
                            <MDBMask overlay="cyan-light" className={styles.project_title} >
                                <p>BSCT</p>
                            </MDBMask>
                        </MDBView>
                    </div>
                    <div className={styles.project}>
                        <a
                            href={"https://github.com/Papatiara/bst-or-bsct"}
                        >
                            <MDBView hover zoom>
                                <img
                                    className={styles.image}
                                    src={portfolio}
                                    alt=""
                                />
                                <MDBMask overlay="cyan-light" className={styles.project_title} >
                                    <p>patriciashirazi.com</p>
                                </MDBMask>
                            </MDBView>
                        </a>
                    </div>
                </div>
            </div>
        </div>

    )
}
const fadeInImage = keyframes` {
    from { opacity: 0; }
    to   { opacity: 1; }
    // 0% {
    //     width: 190%;
    //     // height: 400px;
    // } 
    // 50% {
    //     width: 40%;
    //     height: 400px;
    // }
}`

let styles = {
    projects_container: css`
        // margin-top: 17%;
        width: 100%;
        height: 100vh;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
    `,
    projects: css`
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-size: 22px;
    `,
    projects_grid: css`
        // border: 3px solid pink;
        width: 40%;
        height: 400px;
        display: flex;
        justify-content: space-around;
        flex-wrap: wrap;
        margin-top: 10px;
    `,
    project: css`
        animation: ${fadeInImage} 1s ease-in;

    `,
    project_title: css`
        color: white;
        cursor: pointer;
        font-size: 1.0em;
        font-weight: bold;
        text-align: center;

    `,
    line: css`
        border-bottom: 1px solid grey;
        padding-bottom: 5px;
        margin: auto;
        width: 100px;
`,

}


export default Projects;
