import React, { useState } from 'react';
import { css } from 'emotion'
import Header from './components/Header';

import Intro from './components/Intro';
import Subjects from './components/Subjects';
import './App.css';
import Moody_text from './components/carousel/Moody_text';

function App() {
  const [clickMoody, setClickMoody] = useState(false);
  if (!clickMoody) {
    return (
      <div className="App">
        <header>
          <Intro />
        </header>
        <div>
          < Header />
          <Subjects showMoody={setClickMoody} />
        </div>
      </div>

    );
  } else {
    <Moody_text />
  }
}

const styles = {
  header: css`
    border: 2px black solid;
    width: 100%;
    height: 100%;
  `
}

export default App;
