import React, { useState } from 'react';

import ScrollBody from './ScrollBody';

import { css } from 'emotion'


const Header = (props) => {
    const scrollToRef = (ref) => {
        window.scrollTo({
            top: ref.current.offsetTop,
            behavior: 'smooth'
        })
    }

    const scrollToJourney = () => {
        scrollToRef(props.journeyRef)
    }
    const scrollToProjects = () => {
        scrollToRef(props.projectsRef)
    }
    const scrollToContact = () => {
        scrollToRef(props.contactsRef)
    }


    return (
            <div id="header" className={styles.head}>
                <div className={styles.name}>PATRICIA SHIRAZI</div>
                <div className={styles.options}>
                    <a>HOME</a>
                    <a onClick={scrollToProjects}>PROJECTS</a>
                    <a onClick={scrollToJourney} >JOURNEY</a>
                    <a>RESUME</a>
                    <a onClick={scrollToContact}>CONTACT</a>
                </div>
            </div>
    )
}

const styles = {
    head: css`
    position: sticky;
    top: 0;
    z-index: 99;
    padding-bottom: 20px;
    background-color: #FFF
    ;
    display: flex;
        justify-content: space-around;
        align-items: center;
        width: 100%;
        height: 50px;
        @media (max-width: 1000px) {
            margin:0;

        },
    `,
    name: css`
    float: right;
    font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    font-size: 22px;
    cursor: pointer;

    `,
    icons: css`
    color: white;
    cursor: pointer;
    font-size: 86px;
    &:hover {
        color: #708090;
    }
    `,
    options: css`
    display: flex;
    justify-content: space-around;
        // border: 3px solid blue;
    width: 40%;
    font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
    font-size: 15px;
    a {
    cursor: pointer;
    }
    `
}

export default Header;
