import React, { useState } from 'react';
import { css } from 'emotion'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleUp } from '@fortawesome/free-solid-svg-icons'

import Projects from './Projects';
import Journey from './Journey';



const Subjects = (props) => {
    const [subject, setSubject] = useState('')

    const onclick = (e) => {
        setSubject(e.target.value);
    }

    return (
        <div className={styles.body_container}>
            <div className={styles.introduction}>
                <h2> Hi, I am Patricia</h2>

                <br>
                </br>
                <p className={styles.hi}> </p>
                <br>
                </br>
                <strong> I have always loved solving problems and helping people. I am a bachelor in Mathematics and use to be a high school teacher for a few years. It was three years ago when I've decided I wanted to move with my career to another field. Since then, I've been building full-stack applications using React, Nodejs, Express, Postgres, Redux, and others. Check out my latest project and a little about my background.</strong>
                {/* <a> <FontAwesomeIcon className={styles.icon} icon={faAngleUp} /> </a> */}

                {/* <div id={"here"} className={styles.buttons_container}>
                <button onClick={onclick} className={styles.button} value="All"> All </button>
                <button onClick={onclick} className={styles.button} value="Math"> Math </button>
                <button onClick={onclick} className={styles.button} value="Google"> Google </button>
                <button onClick={onclick} className={styles.button} value="Engineering"> Software Engineering </button>
                <button onClick={onclick} className={styles.button} value="Entrepreneurship"> Entrepreneurship </button>
            </div> */}
                {/* <div className={styles.grid}>
                < Carousel matter={subject} showMoody={props.showMoody} showSubject={props.showSubject} showGoogle={props.showGoogle} showGraduation={props.showGraduation}/>
            </div> */}
            </div>
            <Projects />
            <Journey />
        </div>
    )
}

const styles = {
    body_container: css`
        width: 100%;
        // height: 100vh;
        display: flex;
        flex-direction: column;
        justify-content: center;
        align-items: center;
        background-color: #F5F5F5;
    `,

    introduction: css`
        // border: 3px solid brown; 
        margin-top: 20px;
        width: 50%;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-size: 22px;
        text-align: center;
    `,
    hi: css`
        border-bottom: 2px solid black;
        padding-bottom: 5px;
        margin: auto;
        width: 70px;
    `,
    buttons_container: css`
        margin-top: 5%;
        margin-left: 10.5%;
        flex-wrap: wrap;
        width: 1000px;
        display: flex;
        justify-content: space-between;
        @media (max-width: 1400px) {
            margin-top: 5%;
            transition: 1s;
            width: 300px;
        }
        `,
    button: css` 
        border: 2px solid black;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
        &:hover {
         background-color: black;
        color: white;
        transition: 1s;
         border-color:#DDDDDD;
        }&:focus {
            outline: none;
             background-color: black;
            color: white;
        }@media (max-width: 1400px) {
            margin-top: 3%;
        }
        `,
}
export default Subjects;
