import React from 'react';
import { css } from 'emotion'


const ButtonMath = (props) => {

   const onclick = () =>{
       if(props.Math === true) {
        props.setMath(false)
       } 
       props.setMath(true)
   }

    return (
        <div className={styles.buttons_container}>
            <button onClick={onclick} className={styles.button}> Math </button>
            <button className={styles.button}> Google </button>
            <button className={styles.button}> Software Engineering </button>
            <button className={styles.button}> Entrepreneurship </button>
        </div>
    )
}
const styles = {
    buttons_container: css`
        margin-top: 25%;
        margin-left: 10%;
        width: 700px;
        display: flex;
        justify-content: space-between;
        `,
    button: css` 
        border: 2px solid black;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
        &:hover {
        窶ッackground-color: black;
        color: white;
        transition: 1s;
        窶ッorder-color:#DDDDDD;
        }
        `,
}
export default ButtonMath;
