import React from 'react';
import { css } from 'emotion'
import ScrollIntoView from 'react-scroll-into-view'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown } from '@fortawesome/free-solid-svg-icons'
import SplitText from 'react-pose-text';


import me from '../images/me.jpeg'


let intViewportHeight = window.innerHeight;
let toString = intViewportHeight.toString() + "px"

const charPoses = {
    exit: { opacity: 0, y: 20 },
    enter: {
        opacity: 1,
        y: 0,
        delay: ({ charIndex }) => charIndex * 30
    }
};

const Intro = () => {
    return (
            <div className={styles.intro_holder}>
                <div className={styles.center}>
                    <div>
                        <img src={me} className={styles.img} />
                    </div>
                    <div className={styles.name_title}>
                        <div className={styles.name}>
                            <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
                                Patricia Shirazi
                </SplitText>
                        </div>
                        <div className={styles.title}>
                            <SplitText initialPose="exit" pose="enter" charPoses={charPoses}>
                                full-stack software engineer
                </SplitText>
                        </div>
                    </div>
                </div>

                <ScrollIntoView  smooth={true }selector="#header" className={styles.scroll} >
                    <a className={styles.icon}> <FontAwesomeIcon icon={faAngleDown} /> </a>
                </ScrollIntoView>
        </div>
    )
}

const styles = {
    intire_intro: css`
        margin: 0;
        height:100vh;
        width:100vw
        border: 3px solid red;

    `,
    intro_holder: css`
    height:100vh;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #F5F5F5;
    border: 3px solid blue;

    // background-color: black;

    `,
    center: css`
        border: 3px solid red;
        display: flex;
        justify-content: space-around;
        width: 50%;
        height: 300px;
    `,
    name_title: css`
        // border: 3px solid blue;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
    `,
    name: css`
        font-size: 46px;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-weight: 200;
        text-shadow: 2px 2px 0px rgba(0, 0, 0, 0.2);
    `,
    title: css`
        font-size: 26px;
        font-family: PT Sans, "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
        font-weight: 200;
        text-shadow: 2px 2px 0px rgba(0, 0, 0, 0.2);
    `,
    img: css`
        border-radius: 50%;

    // border: 3px solid black;

    `,
    scroll: css`
    background-color: #F5F5F5;
    padding-top: 50px;
    display: flex;
    align-items: center;
    justify-content: center;
      `,
      icon: css`
        font-size: 24px;
      `
}

export default Intro;
