import React, { useState } from 'react';
import { css } from 'emotion'

import Graduation from './Graduation';
import Business from './Business';
import Projects from './Projects';
import Google from './Google';
import Math from './Math';
import ButtonMath from './ButtonMath';





const Grid = () => {
    const [isMath, setIsMath] = useState(false)
    const [isGoogle, setIsGoogle] = useState(false)


    if (isMath) {
        {console.log('hi', isMath)}
        return (
            <div>
                <ButtonMath setMath={setIsMath} math={isMath} setGoogle={setIsGoogle}/>
                <div className={styles.grid_selected}>
                    < Graduation />
                    < Business />
                </div>
            </div>
        )
    }  if (isGoogle) {
        {console.log('hi', isGoogle)}
        return (
            <div>
                <ButtonMath setGoogle={setIsGoogle} google={isGoogle} setMath={setIsMath}/>
                <div className={styles.grid_selected}>
                    < Google />
                    < Projects />
                </div>
            </div>
        )
    } return (
        <div>
            <ButtonMath setGoogle={setIsGoogle} setMath={setIsMath} math={isMath}/>
            {/* <button onClick={() => setIsMath(true)}>Math</button> */}
            <div className={styles.grid}>
                < Graduation />
                < Business />
                < Projects />
                < Google />
                < Math />
            </div>
        </div>
    )

}

const styles = {
    grid: css`
    display: flex;
    justify-content:space-between;
    flex-wrap: wrap;
    width: 1200px;
    transition: width 2s;
    height: auto;
    margin-top: 10%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1500px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    grid_selected: css `
    display: flex;
    justify-content:space-between;
    opacity: 1; 
    width: 550px;
    transition: width 2s opacity 0.3s;
    height: auto;
    margin-top: 10%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 550px;
        transition: height 2s;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default Grid;
