import React from 'react';
import TextLoop from "react-text-loop";
import { css } from 'emotion'


import maname from '../images/maname.png'


const symbol = "<"

const Maname = () => {
    return (
        <div>
            <div></div>
            <div className={styles.container}>
                <h1 className={styles.maname}> Olá, </h1>
                <h3 className={styles.name}> I am Patricia and this is my portfolio. Click or scroll to know a little more about me </h3>
                <button className={styles.button} > About me </button>
            </div>
            <h2 className={styles.text}>
                <TextLoop springConfig={{ stiffness: 70, damping: 31 }}
                    adjustingSpeed={500} >
                    <span> Software Engineering </span>
                    <span> Mathematics </span>
                    <span> Entrepreneurship </span>
                    <span> User Experience </span>
                </TextLoop>{" "}
            </h2>
        </div>
    )
}
export default Maname;

const styles = {
    container: css`
    position: absolute;
    top: 50%;
    left: 50%;
    -ms-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    margin: 0 auto;
    `,
    maname: css`
    font-size: 67px;
    color: black;
    padding: 10px;
    font-family: 'Libre Franklin', sans-serif;
    `,
    name: css`
    font-size: 23px;
    font-family: 'Libre Franklin', sans-serif;
    `,
    text: css`
    margin-right: 40%;
    color: gray;
    `,
    header: css`
    background-color: black;
    height: 60px;
    padding: 10px;
    color: white;
    `,
    button: css` 
    position: absolute;
    border-radius: 5px;
    top: 85%;
    padding: 14px 28px;
    cursor: pointer;
    &:hover {
    color: white;
     background-color: black;
    transition: 1s;
     border-color:#DDDDDD;
    }&:focus {
        outline: none;
         background-color: black;
        color: white;

    }
    `,
}
