import React from 'react';
import { css, keyframes } from 'emotion'
import { MDBView, MDBMask } from "mdbreact";

import graduation from '../../images/graduation.jpg'
import graduation2 from '../../images/graduation2.jpg'

const width = window.innerWidth;


const Graduation = () => {
    return (
        <div className={styles.graduation}>
            <MDBView hover zoom>
                <img
                    id={"graduation"}
                    className={styles.image}
                    // src={graduation}
                    alt=""
                />
                <MDBMask overlay="cyan-light" className={styles.classname} >
                    <p>Graduation</p>
                </MDBMask>
            </MDBView>
        </div>
    )

}





let classname = "flex-center";

const fadeInImage = keyframes` {
    0% {
        width: 30%;
        height: 30%;
    } 
    20% {
        width: 50%;
        height: 50%;
    } 
    100% {
        width: 100%;
        width: 100%;
    }
}`


const styles = {
    graduation: css`
    // height: 250px;
    // width: 250px;
    // cursor: pointer;
    // @media (max-width: 800px) {
    //     height: 490px;
    //     width: 470px;
    // }
    `,
    image: css`
    height: 250px;
    width: 250px;
    cursor: pointer;
    animation: ${fadeInImage} 0.2s ease-out;
    background-image: url(${graduation});
    @media (max-width: 800px) {
        animation: ${fadeInImage} 0.2s ease-out;
        height: 490px;
        width: 470px;
        background-image: url(${graduation2});
    }
    `,
    classname: css`
    color: white;
    font-size: 1.5em;
    font-weight: bold;
    cursor: pointer;
    `
}
export default Graduation;
