import React, { useState } from 'react';
import { css } from 'emotion'

import Graduation from './Graduation';
import Business from './Business';
import Projects from './Projects';
import Google from './Google';






const Grid = () => {
    const [isMath, setIsMath] = useState(false)
    if (isMath === true) {
        return (
            <div>
                <button onClick={() => setIsMath(true)}>Math</button>
                <div className={styles.grid_selected}>
                    < Graduation />
                    < Business />
                </div>
            </div>
        )
    } return (
        <div>
            <button onClick={() => setIsMath(true)}>Math</button>
            <div className={styles.grid}>
                < Graduation />
                < Business />
                < Projects />
                < Google />
            </div>
        </div>
    )

}

const styles = {
    grid: css`
    display: flex;
    justify-content:space-between;
    width: 1200px;
    height: auto;
    margin-top: 30%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1200px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `,
    grid_selected: css `
    display: flex;
    justify-content:space-between;
    width: 550px;
    transition: 2s;
    height: auto;
    margin-top: 30%;
    margin-left: 10%;
    @media (max-width: 800px) {
        display: flex;
        flex-wrap: wrap;
        height: 1200px;
        width: 250px;
        align-items: center;
        justify-content: space-around;
    }
    `
}
export default Grid;
