import React, { useState } from 'react';
import { css } from 'emotion'
import ScrollIntoView from 'react-scroll-into-view'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDown } from '@fortawesome/free-solid-svg-icons'
import './App.css';


import {
  BrowserRouter as Router,
  Route,
} from "react-router-dom";




import Header from './components/Header';
import Intro from './components/Intro';
import ScrollBody from './components/ScrollBody';


import Moody_text from './components/articles/Moody_text';
import Business_text from './components/articles/Business_text';
import Google_text from './components/articles/Google_text';
import Footer from './components/Footer';
import Graduation_text from './components/articles/Graduation_text';


let intViewportHeight = window.innerHeight;
let toString = (intViewportHeight - 100).toString() + "px"

function App() {
  const [seeMore, setSeeMore] = useState(false)



  if (!seeMore) {
    return (
      <Router>
        <Route path="/">
          <div className={styles.firstbody}>
            <Intro arrowClick={setSeeMore} />
            <div className={styles.body}>
              <Header />
                <ScrollBody showMoody={setClickMoody} showSubject={setClickBusiness} showGoogle={setCLickGoogle} showGraduation={setCLickGraduation} />
              <Footer />
            </div>
          </div>
        </Route>
      </Router>
    )
  }
  else {
    return (
      <Router>
        <div className={styles.firstbody}>
          <Intro arrowClick={setSeeMore} />
          <div className={styles.body}>
            <Header />
            <ScrollBody arrowClick={setSeeMore} showMoody={setClickMoody} showSubject={setClickBusiness} showGoogle={setCLickGoogle} showGraduation={setCLickGraduation} />
            <Footer />
          </div>
        </div>
      </Router>
    )
  }
}

const styles = {

  body: css`
          margin-top: 0%;
          `,
  button: css`
          position: absolute;
          font-family: 'Zilla Slab', serif;
          font-size: 17px;
          outline: 0;
    top: ${toString};
        left: 45%;
        border-radius: 5px;
        padding: 14px 28px;
        cursor: pointer;
  &:hover {
          color: white;
        background-color: black;
        transition: 1s;
        border-color:#DDDDDD;
  }&:focus {
          outline: none;
        background-color: black;
        color: white;
          }
          `,

}

export default App;
